sys_variable_value.value:
(function execute(inputs, outputs) {

var attRec = inputs.attachmentID;
var gr = new GlideRecord('sys_attachment');
gr.addQuery('sys_id', attRec);
gr.query();
if(gr.next()){
var attachment = new GlideSysAttachment();
var encodedAudio = attachment.getContentBase64(gr);
var reqBody =  {
	"audio" : {
    	"content" : encodedAudio

    },
  	"config":{
   		 "encoding" : "LINEAR16",
      	 "languageCode" : "en-US",
         "profanityFilter" : true,
      	 "speechContexts": [{
                  "phrases": ["ServiceNow"]
    			}]
    }
};
gs.info(JSON.stringify(reqBody));



 var r = new sn_ws.RESTMessageV2('x_snc_gc_speech_to.Google Cloud text to Speech', 'recognize');
 r.setRequestBody(JSON.stringify(reqBody));
//override authentication profile 
//authentication type ='basic'/ 'oauth2'
//r.setAuthenticationProfile(authentication type, profile name);

//set a MID server name if one wants to run the message on MID
//r.setMIDServer('MY_MID_SERVER');

//if the message is configured to communicate through ECC queue, either
//by setting a MID server or calling executeAsync, one needs to set skip_sensor
//to true. Otherwise, one may get an intermittent error that the response body is null
//r.setEccParameter('skip_sensor', true);

 var response = r.execute();
 var responseBody = response.getBody();
 var httpStatus = response.getStatusCode();

 gs.info(JSON.stringify(responseBody));
 var repData = JSON.parse(responseBody);
 outputs.recognizedtext = repData.results[0].alternatives[0].transcript;
 outputs.confidence = repData.results[0].alternatives[0].confidence;

}


})(inputs, outputs);

