	// Add your code here
	return '';  // return the calculated value

})(current);

sys_variable_value.value:
(function execute(inputs, outputs) {
var gr = new GlideRecord('sys_attachment');
gr.addQuery('sys_id', inputs.attachmentID);
gr.query();
if(gr.next()){
var attachment = new GlideSysAttachment();
var encodedAudio = attachment.getContentBase64(gr);
outputs.requestbody =  {
	"audio" : {
    	"content" : encodedAudio
    },
  	"config":{
   		 "encoding" : "LINEAR16",
         "sampleRateHertz": 16000,
      	 "languageCode" : "en-US"
    }
};


  outputs.convertedb64audio = encodedAudio;


gs.info(JSON.stringify(outputs.requestbody));
}



})(inputs, outputs);

